itemDisplay.script:
idx = getQueryItemIndex();
drawState = getDrawingItemState();
if (drawState == "unfocus")
{
	border = "/usr/local/etc/mos/www/modules/core/rss/skins/default/unfocus.png";
	color = "255:255:255";
}
else
{
	border = "/usr/local/etc/mos/www/modules/core/rss/skins/default/focus_h_tile.png";
	color = "0:0:0";
}

mediaDisplay.onUserInput:
ret = "false";
i = getFocusItemIndex();
userInput = currentUserInput();

if (userInput == "up")
{
	if( ( i % 5 ) == 0 ) ret = "true";
}
else if (userInput == "down")
{
	if( ( ( i - -1 ) % 5 ) == 0 ) ret = "true";
}

else if (userInput == "edit" || userInput == "video_frwd")
{
        url = "http://127.0.0.1/?page=rss_services_menu";
	url = doModalRss(url);
	if(( url != null )&&( url != "" ))
	{
		moUrl = url;
		savedItem = i;
		setRefreshTime(1);
	}
	ret = "true";
}
ret;

rss.onRefresh:
setRefreshTime(-1);
showIdle();
itemCount = 0;

dlok = getURL( moUrl );
if (dlok != null) dlok = readStringFromFile( dlok );
if (dlok != null)
{
	c = 0;
	pageTitle = getStringArrayAt(dlok, c); c += 1;
	itemCount = getStringArrayAt(dlok, c); c += 1;

	nameArray = null;
	imgArray  = null;
	urlArray  = null;

	count = 0;
	while( count != itemCount )
	{
		nameArray = pushBackStringArray( nameArray, getStringArrayAt(dlok, c)); c += 1;
		imgArray  = pushBackStringArray( imgArray,  getStringArrayAt(dlok, c)); c += 1;
		urlArray  = pushBackStringArray( urlArray,  getStringArrayAt(dlok, c)); c += 1;

		count += 1;
	}
}
if( itemCount == 0 )
{
	setFocusItemIndex( 0 );
}
else if( savedItem > ( itemCount - 1 ))
{
	setFocusItemIndex( itemCount - 1 );
}
else setFocusItemIndex( savedItem );

cancelIdle();
redrawDisplay();

item_template.onClick:
idx = getFocusItemIndex();
url = getStringArrayAt(urlArray , idx);
ret = doModalRss(url);
if(( ret != null )&&( ret != "" ))
{
	moUrl = ret;
	savedItem = idx;
	setRefreshTime(1);
}
null;
