mediaDisplay.onUserInput:
input = currentUserInput();
print("fullnews Got user input: ", input);
if(input == "down")
{
	nextPage = 0;
	ret = nextMultiPage();
	if(ret == "1")
	{
		textPageIndex = Add(textPageIndex, 1);
		nextPage = 1;
	}
	pageIndex = Add(pageIndex, nextPage);
	redrawDisplay();
	"true";
}
else if(input == "up")
{
print("textPageIndex , pageIndex , imgCount , imgPageIndex  ", textPageIndex, pageIndex, imgCount, imgPageIndex);
	prePage = 0;
	if(textPageIndex == pageIndex)
	{
		if(textPageIndex > 0)
		{
			ret = preMultiPage();
			printf("ret : ", ret);
			if(ret == "1")
			{
				textPageIndex = Minus(textPageIndex, 1);
				prePage = 1;
				textReachEnd = 0;
			}
		}
	}
	pageIndex = Minus(pageIndex, prePage);
	redrawDisplay();
	"true";
}
else if(input == "enter")
{
	print("ignore this");
	"true";
}
else if (input == "setup" || input == "guide")
{
	drawOnce = 0;
	"false";
}
else if (input == "left" || input == "right")
{
	"true";
}
else
{
	"false";
}

text.script:
fin = "";
ret = isMultiPageReachEnd();
if(ret == "1")
{
	textReachEnd = 1;
}
if(textReachEnd == 1 && imgReachEnd == 1)
{
	fin = "  (Íê)";
	fin = "  (end)";
}
pageNum = Add(pageIndex, 1);
/*"µÚ " + pageNum + " Ò³" + fin;*/
"Page " + pageNum  + fin;

rss.onEnter:
	drawOnce = 0;

	pageIndex = 0;
	textPageIndex = 0;
	textPageCount = 0;
	textReachEnd = 0;
	totalItem = 0;
	fullnews = getEnv("popuptext");;
