image.script:
if ((type == "hottags")||(type == "relatedtags")){
	if (getFocusItemIndex() > 11)
		"../image/Flcikr_Hint_Arrow_Left.png";
	else
		null;
}
else{
	if (getFocusItemIndex() > 0)
		"../image/Flcikr_Hint_Arrow_Left.png";
	else
		null;
}

if ((type == "hottags")||(type == "relatedtags")){
	focuscolumn=0;
	totalcolumn=0;
	ii=1+getFocusItemIndex();
	while(ii > 6){
		focuscolumn=1+ focuscolumn;
		ii=ii-6;
	}
	ii=itemSize;
	while(ii > 6){
		totalcolumn=1+ totalcolumn;
		ii=ii-6;
	}		
	if ((totalcolumn-focuscolumn) < 2)
		null;
	else
		"../image/Flcikr_Hint_Arrow_Right.png";
}
else{
	if ((getFocusItemIndex()+1) < itemSize)
		"../image/Flcikr_Hint_Arrow_Right.png";
	else
		null;
}

text.script:
if (type == "hottags")
	"Explore > Hot Tags";
else if(type == "mostInterested")
	"Explore > Interesting > Last 7 days";
else if(type == "searchphoto")
	"Search Result > "+keyword;
else if(type == "taggedphotos")
	"Explore > Hot Tags > "+header;
else
	header;

onUserInput.script:
     		userInput = currentUserInput();
			ret = "false";
			if (userInput=="edit"){
				ret = "true";
			}

			else if (type == "slideShow") {
				if (userInput == "up") {
					type = "mostInterested";
					setRefreshTime(-1);
				}
			}
			
			else if (userInput=="pageup"){
				if(page > 1){
					page=page - 1;
					setRefreshTime(1);
				}
				
			}
			else if (userInput=="pagedown"){
				if(page < totalpage){
					page=1 + page;
					setRefreshTime(1);
				}
			}
			else if(getPageInfo("majorContext")=="items" && userInput == "enter"){
				if(type=="hottags"){
					type="taggedphotos";
					setRefreshTime(1);
					ret = "true";
				}
			}
			else if(getPageInfo("majorContext")=="items" && userInput == "right"){
				if(getFocusItemIndex()==4){
					postMessage("pagedown");
				}
			}
			else if(getPageInfo("majorContext")=="items" && userInput == "left"){
				if(getFocusItemIndex()==0){
					postMessage("pageup");
				}
			}
			else if(userInput == "return"){
				print("=======================on return.............");
				if(type=="taggedphotos"){
					type = "hottags";
					resetViewArea = "yes";
					header = "Hot tags";
					Flickr_OnReturn();
					titleArray = Flickr_GetHotTagList();
					itemSize = Flickr_GetItemCount();
					page=1;
					totalpage=1;
					redrawDisplay();
					ret = "true";
				}
			}

			ret;

rss.script:
header = "Flickr";
page = 1;
Rootpage=0;
totalpage=1;
type = "mostInterested";
itemSize=0;
firstEnter = "yes";
setRefreshTime(1);
Flickr_init();
titleArray  = null;
urlArray     = null;
resetViewArea = "no";

rss.onEnter:
print("enter new flickr.......................");
if(getEnv("viewpage")== "searchtag"){
	keyword= getInput(); 
	if(keyword!=null){
		type="searchtag";
		page=1;
		Rootpage = 1;
		setRefreshTime(1);
	}
}
else if(getEnv("viewpage")== "searchphoto"){
	keyword= getInput(); 
	if(keyword!=null){
		type="searchphoto";
		page=1;
		Rootpage = 1;
		setRefreshTime(1);
	}
}
else if(getEnv("viewpage")== "mostInterested"){
	type="mostInterested";
	page=1;
	Rootpage = 1;
	setRefreshTime(1);
}
else if(getEnv("viewpage")== "hottags"){
	type="hottags";
	page=1;
	Rootpage = 1;
	setRefreshTime(1);
}
unsetEnv("viewpage");

rss.onRefresh:
	if (type == "slideShow") {
		postMessage("right");
		
		index = getFocusItemIndex();
		/*print("Right--------", index);*/
		if (index == 23) {
			postMessage("up");
			postMessage("up");
			postMessage("enter");
		}
	}
	else {
	showIdle();
	if (type == "searchphoto") {
	    print("The search keyword is ", keyword); 
		if(Rootpage == 1)
			res=Flickr_UpdatePage("searchphoto",keyword,page,"rootpage");
		else
			res=Flickr_UpdatePage("searchphoto",keyword,page);
		resetViewArea = "yes";
		header = "Search Result : " + keyword;
		titleArray = Flickr_Getphototitlelist();
		urlArray = Flickr_Getphotourllist();
		itemSize = Flickr_GetItemCount();
		totalpage=Flickr_GetPageCount();
		print("photo search ..... total,pages,",itemSize,totalpage);
		setRefreshTime(-1);
		Rootpage = 0;
		if(getPageInfo("majorContext") == "menu")
			postMessage("down");
		setFocusItemIndex(0);
	}
	else if (type == "searchtag") {
	    print("The search keyword is ", keyword);
		if(Rootpage == 1)
			res=Flickr_UpdatePage("searchtag",keyword,page,"rootpage");
		else
			res=Flickr_UpdatePage("searchtag",keyword,page);
		resetViewArea = "yes";
		header = "Search Result : " + keyword;
		titleArray = Flickr_Getphototitlelist();
		urlArray = Flickr_Getphotourllist();
		itemSize = Flickr_GetItemCount();
		totalpage=Flickr_GetPageCount();
		print("photo search from tag..... total,pages,",itemSize,totalpage);
		setRefreshTime(-1);
		Rootpage = 0;
		if(getPageInfo("majorContext") == "menu")
			postMessage("down");
		setFocusItemIndex(0);
	}
	else if (type == "mostInterested"){
		if(Rootpage == 1)
			res=Flickr_UpdatePage("interested",page,"rootpage");
		else
			res=Flickr_UpdatePage("interested",page);
		resetViewArea = "yes";
        header = "Most Interested Pictures";
		titleArray = Flickr_Getphototitlelist();
		urlArray = Flickr_Getphotourllist();
		itemSize = Flickr_GetItemCount();
		totalpage=Flickr_GetPageCount();
		print("photo..... total,pages,",itemSize,totalpage);
		setRefreshTime(-1);
		Rootpage = 0;
		if(getPageInfo("majorContext") == "menu")
			postMessage("down");
		setFocusItemIndex(0);
	}
	else if (type == "hottags"){
		if(Rootpage == 1)
			res=Flickr_UpdatePage("hottags",page,"rootpage");
		else
			res=Flickr_UpdatePage("hottags",page);
		resetViewArea = "yes";
        header = "Hot tags";
		titleArray = Flickr_GetHotTagList();
		itemSize = Flickr_GetItemCount();
		page=1;
		totalpage=1;
		print("hottags..... total,",itemSize);
		setRefreshTime(-1);
		Rootpage = 0;
		if(getPageInfo("majorContext") == "menu")
			postMessage("down");
		setFocusItemIndex(0);
	}
	else if (type == "taggedphotos"){
		tag=getStringArrayAt(titleArray , -1);
		res=Flickr_UpdatePage("tagphotos",tag,page);
		if(res=="success"){
			resetViewArea = "yes";
			header = tag;
			titleArray = Flickr_Getphototitlelist();
			urlArray = Flickr_Getphotourllist();
			itemSize = Flickr_GetItemCount();
			page=1;
			totalpage=1;
			print("taggedphotos..... total,pages,",itemSize);
			Rootpage = 0;
		}
		else
			type="hottags";
		setRefreshTime(-1);
		setFocusItemIndex(0);
	}
		if (firstEnter != "yes")
		{
			redrawDisplay();
		}
		else
		{
			firstEnter = "no";
		}
	}

playURL.script:
url = getStringArrayAt(urlArray , -1);
print("picture URL = ", url);		
url;

onClick.script:
keyword= getInput(); 
type = "searchphoto";
page = 1;
if (keyword!=null) { 
	setRefreshTime(1);
}
else {
  url = null;
}
redrawDisplay();
