rss.script:
	print("////////////////////////////////////mediafly_AddNewUserPin.rss/////////////////////////////////");
	 setParentFade(100);
	displayTitleArray = null;
	displayTitleArray = pushBackStringArray(displayTitleArray,"Enter Username");
	displayTitleArray = pushBackStringArray(displayTitleArray,"Enter Password");
	displayTitleArray = pushBackStringArray(displayTitleArray,"Enter PIN");
	displayTitleArray = pushBackStringArray(displayTitleArray,"Submit");
	itemSize = 4;
	print("display:",displayTitleArray);

	
	setFocusItemIndex(0);

	retArray = null;
	userName = null;
	passwd = null;
	pin = null;

rss.onUpdateMenu:
	
	if (userName!=null || passwd!=null || pin!=null)
	{
		displayTitleArray = null;
		if (userName!=null)
			displayTitleArray = pushBackStringArray(displayTitleArray,"Username: "+userName);
		else
			displayTitleArray = pushBackStringArray(displayTitleArray,"Enter Username");

		if (passwd!=null)
			displayTitleArray = pushBackStringArray(displayTitleArray,"Password: ********");
		else
			displayTitleArray = pushBackStringArray(displayTitleArray,"Enter Password");

		if (pin!=null)
			displayTitleArray = pushBackStringArray(displayTitleArray,"PIN: ****");
		else
			displayTitleArray = pushBackStringArray(displayTitleArray,"Enter PIN");
			
		displayTitleArray = pushBackStringArray(displayTitleArray,"Submit");

		redrawDisplay();
	}

onUserInput.script:
      userInput = currentUserInput();
      itemFocusState = getDrawingItemState();
      print(" input key: ", userInput);
      ret = "false";

      if (userInput=="return")
      {
		setReturnString(retArray);
      }
      
      ret;

image.script:
			index1 = getQueryItemIndex();
			
			index2 =getFocusItemIndex ();

			if (index1 == index2)
			{
			
				 "./IMS_Modules/Mediafly/image/mediafly_dialog_focus.fsp";
			
			}
			else
			{
			
				  "./IMS_Modules/Mediafly/image/mediafly_dialog_unfocus.fsp";
			}

foregroundColor.script:
fgcolor = "51:51:51";
queryIndex = getQueryItemIndex();
focusIndex = getFocusItemIndex();
if (queryIndex == focusIndex) {
	fgcolor = "0:143:18";
}
fgcolor;

text.script:
index = getQueryItemIndex();


str = getStringArrayAt(displayTitleArray , index);

str;

item_template.onClick:
    
      selectedIndex = getFocusItemIndex();
      print("selectedIdex: ",selectedIndex);

      if (selectedIndex==0)
      	{
      		userName = getInput( );
      		print("userName=",userName);
      		if (userName!=null)
	      		executeScript("onUpdateMenu");
      		
      	}
      else if (selectedIndex==1)
      		{
      			passwd = getInput("passWord" );
			print("passwd=",passwd);
			if (passwd!=null)
				executeScript("onUpdateMenu");
      		}
      		else if (selectedIndex==2)
      			{
      				pin = getInput( "passWord");
      				print("pin=",pin);
      				if (pin!=null)
	      				executeScript("onUpdateMenu");
      				
      			}
	      		else if (selectedIndex==3)
	      			{
	      				if (userName!=null)
	      					retArray = pushBackStringArray(retArray,userName);
	      				else
	      					retArray = pushBackStringArray(retArray,"null");
	      					
	      				if (passwd!=null)
	      					retArray = pushBackStringArray(retArray,passwd);
	      				else
	      					retArray = pushBackStringArray(retArray,"null");

	      				if (pin!=null)
	      					retArray = pushBackStringArray(retArray,pin);
	      				else
	      					retArray = pushBackStringArray(retArray,"null");

	      				postMessage("return");
	      				
	      			}
      null;
