mediaDisplay.onUserInput:
		input = currentUserInput();
		print("fullnews Got user input: ", input);
		if(input == "down")
		{
			nextPage = 0;
			ret = nextMultiPage();
			if(ret == "1")
			{
				textPageIndex = Add(textPageIndex, 1);
				nextPage = 1;
			}
			if(imgCount > 0)
			{
				imgTotal = Minus(imgCount, 1);
				if(imgPageIndex < imgTotal)
				{
					imgPageIndex = Add(imgPageIndex, 1);
					nextPage = 1;
					if(imgPageIndex == imgTotal)
					{
						imgReachEnd = 1;
					}
				}
			}
			pageIndex = Add(pageIndex, nextPage);
			redrawDisplay();
			"true";
		}
		else if(input == "up")
		{
		print("textPageIndex , pageIndex , imgCount , imgPageIndex  ", textPageIndex, pageIndex, imgCount, imgPageIndex);
			prePage = 0;
			if(textPageIndex == pageIndex)
			{
				if(textPageIndex > 0)
				{
					ret = preMultiPage();
					printf("ret : ", ret);
					if(ret == "1")
					{
						textPageIndex = Minus(textPageIndex, 1);
						prePage = 1;
						textReachEnd = 0;
					}
				}
			}
			if(imgCount > 0)
			{
				if(imgPageIndex == pageIndex)
				{
					if(imgPageIndex > 0)
					{
						imgPageIndex = Minus(imgPageIndex, 1);
						prePage = 1;
						imgReachEnd = 0;
					}
				}
			}
			pageIndex = Minus(pageIndex, prePage);
			redrawDisplay();
			"true";
		}
		else if(input == "left" || input == "right")
		{
			totalItemCount = Minus(totalItem, 1);
			if((input == "left" && itemIndex > 0) ||
			(input == "right" && itemIndex < totalItemCount))
			{
				showIdle();
				if(input == "left")
				{
					itemIndex = Minus(itemIndex, 1);
				}
				else
				{
					itemIndex = Add(itemIndex, 1);
				}
				print("itemIndex is :", itemIndex);

				resetMultiPage();

				pageIndex = 0;
				textPageIndex = 0;
				textPageCount = 0;
				imgPageIndex = 0;
				imgCount = 0;
				textReachEnd = 0;
				imgReachEnd = 0;

				fullnews = null;
				imgArray = null;
				path = getStoragePath("tmp") + "tmp.rss";
				dlok = loadXMLFile(path);
				if(dlok != null)
				{
					totalItem = getXMLElementCount("rss", "channel", "item");
					str = getXMLText("rss", "channel", "item", itemIndex, "description");
					image = extractSubString(str, "http://", ".jpg");
					capital = 0;
					if (image == null)
					{
						image = extractSubString(str, "http://", ".JPG");
						capital = 1;
					}
					imgReachEnd = 1;
					if (image != null)
					{
						imgCount = 1;
						if (capital == 0)
						{
							image = image + ".jpg";
						}
						else
						{
							image = image + ".JPG";
						}
						/* extract the substring following gt symbol */
						str = extractSubString(str, ">", "", "false");
						imgArray = pushBackStringArray(imgArray, image);
					}
					else
					{
						imgCount = 0;
					}
		
					/* remove unused symbol */
					str = filteroutString(str, "<br>");
					str = filteroutString(str, "<author>");
					str = filteroutString(str, "</author>");
					str = filteroutString(str, "<category>");
					str = filteroutString(str, "</category>");
					str = filteroutString(str, "<lastBuildDate>");
					str = filteroutString(str, "</lastBuildDate>");
					fullnews = pushBackStringArray(fullnews, str);
				}
				if(fullnews == null)
				{
					fullnews = "NIL";
				}
				cancelIdle();
			}
			redrawDisplay();
			"true";
		}
		else if(input == "enter")
		{
			print("ignore this");
			"true";
		}
		else
		{
			"false";
		}

widthPC.script:
if((imgCount > 0) && (imgPageIndex == pageIndex))
{
	if(textPageIndex < pageIndex)
	{
		"0";
	}
	else
	{
		"34";
	}
}
else
{
	"72";
}

text.script:
fin = "";
ret = isMultiPageReachEnd();
if(ret == "1")
{
	textReachEnd = 1;
}
if(textReachEnd == 1 && imgReachEnd == 1)
{
	fin = "  (Íê)";
}
pageNum = Add(pageIndex, 1);
"µÚ " + pageNum + " Ò³" + fin;

rss.onEnter:
	pageIndex = 0;
	textPageIndex = 0;
	textPageCount = 0;
	imgPageIndex = 0;
	imgCount = 0;
	textReachEnd = 0;
	imgReachEnd = 0;
	totalItem = 0;

	fullnews = null;
	imgArray = null;
	path = getStoragePath("tmp") + "newsItemIndex.dat";
	itemIndex = readStringFromFile(path);
	print("itemIndex is :", itemIndex);
	path = getStoragePath("tmp") + "tmp.rss";
	dlok = loadXMLFile(path);
	if(dlok != null)
	{
		totalItem = getXMLElementCount("rss", "channel", "item");
		str = getXMLText("rss", "channel", "item", itemIndex, "description");
		image = extractSubString(str, "http://", ".jpg");
		capital = 0;
		if (image == null)
		{
			image = extractSubString(str, "http://", ".JPG");
			capital = 1;
		}
		imgReachEnd = 1;
		if (image != null)
		{
			imgCount = 1;
			if (capital == 0)
			{
				image = image + ".jpg";
			}
			else
			{
				image = image + ".JPG";
			}
			/* extract the substring following gt symbol */
			str = extractSubString(str, ">", "", "false");
			imgArray = pushBackStringArray(imgArray, image);
		}
		else
		{
			imgCount = 0;
		}
		
		/* remove unused symbol */
		str = filteroutString(str, "<br>");
		str = filteroutString(str, "<author>");
		str = filteroutString(str, "</author>");
		str = filteroutString(str, "<category>");
		str = filteroutString(str, "</category>");
		str = filteroutString(str, "<lastBuildDate>");
		str = filteroutString(str, "</lastBuildDate>");
		fullnews = pushBackStringArray(fullnews, str);
	}
	if(fullnews == null)
	{
		fullnews = "NIL";
	}
