rss.onRefresh:
counter = Add(counter, 1);
if ((timeTick%10) == 0)
{
	print("...... update device info ......");
	itemSize = 0;
	hasDevice = "no";
	titleArray = null;
	sizeArray = null;
	mountArray = null;
	result = Thunder_getDeviceInfo();
	if (result != null)
	{
		print("======> device info: ", result);
		hasDevice = "yes";
		itemSize = getStringArrayAt(result, 0);
		count = 0;
		index = 1;
		while (1)
		{
			if (count >= itemSize)
			{
				break;
			}
			titleArray = pushBackStringArray(titleArray, getStringArrayAt(result, index));
			index = Add(index, 1);
			mountArray = pushBackStringArray(mountArray, getStringArrayAt(result, index));
			index = Add(index, 1);
			sizeArray = pushBackStringArray(sizeArray, getStringArrayAt(result, index));
			index = Add(index, 1);
			count = Add(count, 1);
		}
		setFocusItemIndex(0);
	}
	redrawDisplay();
	timeTick = 1;
}
else
{
	timeTick = Add(timeTick, 1);
	redrawDisplay("widget");
}

rss.script:
itemSize = 0;
hasDevice = "no";
titleArray = null;
sizeArray = null;
mountArray = null;
result = Thunder_getDeviceInfo();
if (result != null)
{
	print("======> device info: ", result);
	hasDevice = "yes";
	itemSize = getStringArrayAt(result, 0);
	count = 0;
	index = 1;
	while (1)
	{
		if (count >= itemSize)
		{
			break;
		}
		titleArray = pushBackStringArray(titleArray, getStringArrayAt(result, index));
		index = Add(index, 1);
		mountArray = pushBackStringArray(mountArray, getStringArrayAt(result, index));
		index = Add(index, 1);
		sizeArray = pushBackStringArray(sizeArray, getStringArrayAt(result, index));
		index = Add(index, 1);
		count = Add(count, 1);
	}
	setFocusItemIndex(0);
}

mediaDisplay.onUserInput:
index = getFocusItemIndex();
input = currentUserInput();
ret = "false";
if (input == "enter")
{
	/* setup database, create essential symbol link */
	if (itemSize > 0)
	{
		mountPath = getStringArrayAt(mountArray, index);
		print("your selected device: ", mountPath);
		showIdle();
		ready = Thunder_deviceReadyForDownload(index);
		if (ready != null)
		{
			postMessage("return");
			setEnv("thunderDevReady", "yes");
			ret = "true";
		}
		cancelIdle();
	}
	else
	{
		ret = "true";
	}
}
else if (input == "right" || input == "left")
{
	ret = "true";
}
ret;

text.script:
if (hasDevice == "no")
{
	"无存储设备,按返回键进入无盘点播";
}
else
{
	"可用设备列表";
}
