		}

        curpath = null;
		if(IsScanning != 0){
			curpath = GetScanningPath();
		}

        print("DBReady:", DBReady, "CurScanDisk:", CurScanDisk);
        LastDBReady = DBReady;
        LastCurScanDisk = CurScanDisk;
        LastCurScanPath = curpath;
        LastCurphase = curphase;
        if(IsScanning == 0){
        	scantimer = 1000;
        }else{
 			scantimer = 1000;
 		}
        setRefreshTime(scantimer);

mediaDisplay.onUserInput:
		userInput = currentUserInput();

		redrawState = "yes";
		if ("guide" == userInput){
			print("====== ignore guide key ======");
			"true";

		}else if("option_red" == userInput){
			if(CurScanDisk != null){
				StopCurDiskScan();
				"true";
			}else{
				print("====== process key: ", userInput);
				"false";
			}
		}
		else if (userInput == "edit")
		{
			print("ignore edit key");
			"true";
		}
		else if ("option_green" == userInput)
		{
			if (DBReady == "true") {
				if (CurScanDisk == null) {
					RescanDisk();
					IsScanning = 1;
					"true";
				}
				else {
					"false";
				}
			}
			else {
				"false";
			}
		}


		else if ("notification" == userInput){
			print("==");			
			redrawDisplay();
			"true";
		}
		else if(userInput == "one" || userInput == "1")
      		{    
          		setFocusItemIndex(0);
			redrawDisplay();
      		}
      		else if(userInput == "two" || userInput == "2")
      		{
          		setFocusItemIndex(1);
          		redrawDisplay();
      		}
      		else if(userInput == "three" || userInput == "3")
      		{
         		setFocusItemIndex(2);
          		redrawDisplay();
       		}
      		else if(userInput == "four" || userInput == "4")
      		{
         		setFocusItemIndex(3);
          		redrawDisplay();
        	}
      		else if(userInput == "five" || userInput == "5")
      		{
			setFocusItemIndex(4);
			redrawDisplay();
      		}
      		else if(userInput == "six" || userInput == "6")
      		{
          		setFocusItemIndex(5);
          		redrawDisplay();
     		}
      		else if(userInput == "seven" || userInput == "7")
      		{
          		setFocusItemIndex(6);
          		redrawDisplay();
      		}
      		else if(userInput == "eight" || userInput == "8")
      		{
          		setFocusItemIndex(7);
          		redrawDisplay();
      		}			
		else{
			print("====== process key: ", userInput);
			redrawDisplay();
			"false";
		}

