media:thumbnail.script:
thumbnail = getItemInfo("imagePath");
ItemGroup = getItemInfo("group");
state = getDrawingItemState();
if(ItemGroup == "DB"){
	if(DBReady == "false"){
		thumbnail= thumbnail + "_gray";
	}
}
if (state == "unfocus") {
	thumbnail = thumbnail;
}
else if (state== "focus"){
	thumbnail = thumbnail + "_unfocus";
}
else {
	thumbnail = null;
}
if(thumbnail != null){
	thumbnail = thumbnail + image_type;
}

