mediaDisplay.onUserInput:
		userInput = currentUserInput();
		if ("guide" == userInput){
			print("====== ignore guide key ======");
			"true";
		}else if("up" == userInput){
                	"true";
		}else if("video_stop" == userInput){
			if(CurScanDisk != null){
				StopCurDiskScan();
				"true";
			}else{
				print("====== process key: ", userInput);
				"false";
			}
		}
		else if (userInput == "edit")
		{
			print("ignore edit key");
			"true";
		}
		else if ("option_green" == userInput)
		{
			if (DBReady == "true") {
				if (CurScanDisk == null) {
					RescanDisk();
					IsScanning = 1;
					"true";
				}
				else {
					"false";
				}
			}
			else {
				"false";
			}
		}
		else{
			print("====== process key: ", userInput);
			"false";
		}

