itemSize = 0;
querySymbols = "";
symbol = getStringArrayAt(idArray, itemSize);	
while (  symbol != null) {
	itemSize = itemSize + 1;
	symbol = getStringArrayAt(idArray, itemSize);
}	
print("itemp size=**=>",itemSize);

