onUserInput.script:
     		userInput = currentUserInput();
			ret = "false";
			if (userInput=="edit"){
				ret = "true";
			}


				if (userInput == "up") {
					postMessage("left");
					ret = "true";
				}

				if (userInput == "down") {
					postMessage("right");
					ret = "true";
				}


			ret;

