item.onClick:
print("onClick");
StockSymbol = getInput("Enter Ticker Symbol"); 								
if (StockSymbol!=null) { 

	print("add ", StockSymbol, " to favorite");
	symbolArray = pushBackStringArray(symbolArray, StockSymbol);		
	writeStringToFile(storagePath, symbolArray);		 
	itemSize = itemSize + 1;
	setFocusItemIndex(itemSize -1);
	setRefreshTime(100);
	null;
}
else {
	url = null;
}
