setFullScreenFade(64);

popupHeader = "$[NETFLIX_DEACTIVATE]";
popupMessageSize = 2;
popupMessages = pushBackStringArray(popupMessages, "$[NETFLIX_DEACTIVATION_REBOOT]" + " " + "$[NETFLIX_DEACTIVATION_CONFIRM]");
