rss.onEnter:
	pageTitle = getPageInfo("pageTitle");
	print("page title is ", pageTitle);

	changeCategory = getEnv("changeCategory");
	if (changeCategory == "previous" || changeCategory == "next") {
		postMessage("return");
	}

	disableDrawBackBuffer = getEnv("disableDrawBackBuffer");
	if (disableDrawBackBuffer == "true") {
		redrawDisplay("no");
		if (changeCategory == "") {
			setEnv("disableDrawBackBuffer", "false");
		}
	}

	targetTitle = "$[NETWORK]";
	itemTitleCount = getEnv("itemTitleCount");
	itemTitleList = getEnv("itemTitleList");
	itemTitleIndex = 0;
	checkIndex = 0;
	while (1) {
		checkTitle = getStringArrayAt(itemTitleList, checkIndex);
		if (checkTitle == targetTitle) {
			itemTitleIndex = checkIndex;
		}
		checkIndex += 1;
  		if (checkIndex == itemTitleCount){
			break;
  		}
	}

image.script:
if (initFlag == "$[ON]") {
	"./setup_menu/image/setup_p2_box_top_1.fsp";
}
else if (itemTitleCount == 4) {
	"./setup_menu/image/setup_p2_box_top_4.fsp";
}
else if (itemTitleCount == 5) {
	"./setup_menu/image/setup_p2_box_top_5.fsp";
}
else {
	"./setup_menu/image/setup_p2_box_top_6.fsp";
}

offsetXPC.script:
nextPos = hintIconX;
currPos = nextPos;
nextPos += hintIconWidth + hintGap;
currPos;

currPos = nextPos;
nextPos += hintIconWidth + hintGap;
currPos;

currPos = nextPos;
nextPos += hintMsgWidth + hintGap;
currPos;

text.script:
if (initFlag == "$[ON]") {
	nextPos = hintIconX;
	currPos = nextPos;
	"";
}
else {
	"$[CHANGE_CATEGORY]";
}

offsetXPC.script:
currPos = nextPos;
nextPos += hintIconWidth + hintGap;
currPos;

currPos = nextPos;
nextPos += hintMsgWidth + hintGap;
currPos;

image.script:
image = "";
queryIndex = getQueryItemIndex();
focusIndex = getFocusItemIndex();
if (queryIndex == focusIndex) {
	image = "./setup_menu/image/setup_p2_list_focus.fsp";
}
image;

text.script:
data = "";
queryIndex = getQueryItemIndex();
if (queryIndex == 0) {
	data = username;
}
else if (queryIndex == 1) {
	data = "";
}
else if (queryIndex == 2) {
	data = interface;
}
data;

mediaDisplay.onEnter:
hintIconX = 11.4;
hintIconY = 84.7;
hintIconWidth = 2.0;
hintIconHeight = 3.6;
hintGap = 0.9;
hintMsgY = 85.6;
hintMsgWidth = 17.2;
hintMsgHeight = 2.8;

username  = getPPPoEData("USR_NAME");
password  = getPPPoEData("PASSWORD");
interface = getPPPoEData("INTERFACE");

mediaDisplay.onUserInput:
handle = "false";
userInput = currentUserInput();
if ("return" == userInput || "pageup" == userInput) {
	setReturnString("$[NET3POE_INTERFACE]");
	handle = "return";
}
else if ("enter" == userInput || "pagedown" == userInput) {
	username = getInput("Input user name","doModal");
	if(username != NULL) {
		if (username != "") {
			setPPPoEData("USR_NAME", username);
			setReturnString("$[NET3POE_PASSWORD]");
			handle = "return";
		}
		else {
			handle = "true";
		}
	}
	else {
		handle = "true";
	}
}
else if ("left" == userInput) {
	redrawDisplay("no");
	setEnv("disableDrawBackBuffer", "true");
	setEnv("changeCategory", "previous");
	handle = "return";
}
else if ("right" == userInput) {
	redrawDisplay("no");
	setEnv("disableDrawBackBuffer", "true");
	setEnv("changeCategory", "next");
	handle = "return";
}
handle;
