if( title == "$[DEVICE_NAME]" ) {
	getSystemDeviceName();
}
else if( title == "$[SKIN_SEL]" ) {
	getSkinName();
}
else {
	getCurrentSetting(title);
}

mediaDisplay.onEnter:
popupHeader = "$[FORBIDDEN_OPERATION]";
popupMessageSize = 1;
popupMessages = "$[FORMAT_HDD_WARNING_1]";
popupOptionSize = 1;
popupOptions = pushBackStringArray(popupOptions, "OK");

hintIconX = 7.4;
hintIconY = 87.7;
hintIconWidth = 2.0;
hintIconHeight = 3.6;
hintGap = 0.9;
hintMsgY = 87.7;
hintMsgWidth = 17.2;
hintMsgHeight = 3.8;

mediaDisplay.onUserInput:
handle = "false";

userInput = currentUserInput();
if ("left" == userInput) {
	redrawDisplay("no");
	setEnv("disableDrawBackBuffer", "true");
	setEnv("changeCategory", "previous");
	handle = "return";

	userPressLeftRight = "1";
}
else if ("right" == userInput) {
	redrawDisplay("no");
	setEnv("disableDrawBackBuffer", "true");
	setEnv("changeCategory", "next");
	handle = "return";

	userPressLeftRight = "1";
}

	else if ("notification" == userInput){
		print("==");			
		redrawDisplay();
		"true";
	}	

handle;

