rss.onEnter:
	pageTitle = getPageInfo("pageTitle");
	print("page title is ", pageTitle);

	changeCategory = getEnv("changeCategory");
	if (changeCategory == "previous" || changeCategory == "next") {
		postMessage("return");
	}

	disableDrawBackBuffer = getEnv("disableDrawBackBuffer");
	if (disableDrawBackBuffer == "true") {
		redrawDisplay("no");
		if (changeCategory == "") {
			setEnv("disableDrawBackBuffer", "false");
		}
	}

	targetTitle = "$[AUDIO]";
	itemTitleCount = getEnv("itemTitleCount");
	itemTitleList = getEnv("itemTitleList");
	itemTitleIndex = 0;
	checkIndex = 0;
	while (1) {
		checkTitle = getStringArrayAt(itemTitleList, checkIndex);
		if (checkTitle == targetTitle) {
			itemTitleIndex = checkIndex;
		}
		checkIndex += 1;
  		if (checkIndex == itemTitleCount){
			break;
  		}
	}

image.script:
if (initFlag == "$[ON]") {
	"./setup_menu/skin4/image/setup_p2_box_top_1.fsp";
}
else if (itemTitleCount == 4) {
	"./setup_menu/skin4/image/setup_p2_box_top_4.fsp";
}
else if (itemTitleCount == 5) {
	"./setup_menu/skin4/image/setup_p2_box_top_5.fsp";
}
else {
	"./setup_menu/skin4/image/setup_p2_box_top_6.fsp";
}

text.script:
if (itemTitleCount > 4) {
	index = (itemTitleIndex + 4) % itemTitleCount;
	title = getStringArrayAt(itemTitleList, index);
}
else {
	title = "";
}

offsetXPC.script:
nextPos = hintIconX;
currPos = nextPos;
nextPos += hintIconWidth + hintGap;
currPos;

currPos = nextPos;
nextPos += hintIconWidth + hintGap;
currPos;

currPos = nextPos;
nextPos += hintMsgWidth + hintGap;
currPos;

text.script:
if (initFlag == "$[ON]") {
	nextPos = hintIconX;
	currPos = nextPos;
	"";
}
else {
	"$[CHANGE_CATEGORY]";
}

image.script:
image = "";
queryIndex = getQueryItemIndex();
focusIndex = getFocusItemIndex();
if (queryIndex == focusIndex) {
	image = "./setup_menu/skin4/image/setup_p2_list_focus.fsp";
}
image;

text.script:
title = getItemInfo("title");
data = getCurrentSetting(title);
if (title == "$[HDMI_OUTPUT]") {
	if (checkDeviceStatus("HDMI") == "$[OFF]") {
		data = "$[NO_DEVICE]";
	}
}
data;

mediaDisplay.onEnter:
popupHeader = "$[AUDIO_BUFFER_CONFLICT]";
popupMessageSize = 1;
popupMessages = "$[CONFLICT_SPDIF_OUTPUT_1]";
popupOptionSize = 1;
popupOptions = pushBackStringArray(popupOptions, "$[OK]");

popupHeader = "$[AUDIO_BUFFER_CONFLICT_POSSIBLE]";
popupMessageSize = 1;
popupMessages = "$[CONFLICT_SPDIF_OUTPUT_2]";
popupOptionSize = 1;
popupOptions = pushBackStringArray(popupOptions, "$[OK]");

popupHeader = "$[AUDIO_BUFFER_CONFLICT]";
popupMessageSize = 1;
popupMessages = "$[CONFLICT_SPDIF_OUTPUT_3]";
popupOptionSize = 1;
popupOptions = pushBackStringArray(popupOptions, "$[OK]");

popupHeader = "$[AUDIO_BUFFER_CONFLICT_POSSIBLE]";
popupMessageSize = 1;
popupMessages = "$[CONFLICT_SPDIF_OUTPUT_4]";
popupOptionSize = 1;
popupOptions = pushBackStringArray(popupOptions, "$[OK]");

hintIconX = 11.4;
hintIconY = 84.7;
hintIconWidth = 2.0;
hintIconHeight = 3.6;
hintGap = 0.9;
hintMsgY = 85.6;
hintMsgWidth = 17.2;
hintMsgHeight = 2.8;

mediaDisplay.onUserInput:
handle = "false";
userInput = currentUserInput();
if ("left" == userInput) {
	redrawDisplay("no");
	setEnv("disableDrawBackBuffer", "true");
	setEnv("changeCategory", "previous");
	handle = "return";
}
else if ("right" == userInput) {
	redrawDisplay("no");
	setEnv("disableDrawBackBuffer", "true");
	setEnv("changeCategory", "next");
	handle = "return";
}
else if ("enter" == userInput) {
	title = getItemInfo("title");
	if (title == "$[HDMI_OUTPUT]") {
		if (checkDeviceStatus("HDMI") == "$[OFF]") {
			handle = "true";
		}
	}
}
handle;

mediaDisplay.onEnter:
popupHeader = "$[DRC]";
popupOptionSize = 3;
popupOptions = pushBackStringArray(popupOptions, "$[OFF]");
popupOptions = pushBackStringArray(popupOptions, "$[ON]");
popupOptions = pushBackStringArray(popupOptions, "$[COMFORT]");
popupOptionFocus = getCurrentSetting("$[DRC]");

onClick.script:
rss = "rss_file://./setup_menu/skin4/scripts/dynamicPopup.rss";
ret = doModalRss(rss);
if( ret != NULL ) {
	setNightMode(ret);
}

mediaDisplay.onEnter:
popupHeader = "$[HDMI_OUTPUT]";
popupOptionSize = 3;
popupOptions = pushBackStringArray(popupOptions, "LPCM");
popupOptions = pushBackStringArray(popupOptions, "LPCM Multi-Ch");
popupOptions = pushBackStringArray(popupOptions, "RAW");
popupOptionFocus = getCurrentSetting("$[HDMI_OUTPUT]");

onClick.script:
rss = "rss_file://./setup_menu/skin4/scripts/dynamicPopup.rss";
ret = doModalRss(rss);
if( ret != NULL ) {
	setAudioHdmiOutput(ret);
}

if (isMars() == "true") {
	hdmiOutput = getCurrentSetting("$[HDMI_OUTPUT]");
	spdifOutput = getCurrentSetting("$[SPDIF_OUTPUT]");
	if (hdmiOutput == "RAW") {
		if (spdifOutput == "LPCM") {
			doModalRss(rss, "mediaDisplay", "popupDialog", 0);
		}
		else if (spdifOutput == "RAW") {
			doModalRss(rss, "mediaDisplay", "popupDialog", 3);
		}
	}
	else if (hdmiOutput == "LPCM") {
		if (spdifOutput == "RAW") {
			doModalRss(rss, "mediaDisplay", "popupDialog", 1);
		}
	}
	else if (hdmiOutput == "LPCM Multi-Ch") {
		if (spdifOutput == "RAW") {
			doModalRss(rss, "mediaDisplay", "popupDialog", 2);
		}
	}
}
null;

