image.script:
image = "";
queryIndex = getQueryItemIndex();
if (queryIndex == 1) {
	image = "./setup_menu/skin4/image/setup_input_unfoucs_bg.fsp";

}
image;

foregroundColor.script:
fgcolor = "255:255:255";
queryIndex = getQueryItemIndex();
if (queryIndex == 1) {
	fgcolor = "0:0:0";
}
fgcolor;

mediaDisplay.onEnter:
popupHeader = "$[CONFIRM]";
popupMessageSize = 0;
popupMessages = "";
popupOptionSize = 2;
popupOptions = pushBackStringArray(popupOptions, "$[YES]");
popupOptions = pushBackStringArray(popupOptions, "$[NO]");
popupOptionWide = 1;

setFullScreenFade(64);

lipSyncValue = getHdmiLipSyncValue();
print("[toto] audioLipSyncManualrss -- get lip-sync value=", lipSyncValue);

setFocusItemIndex(1);
setItemFocus(1);
redrawDisplay("item", 1);
postMessage("enter");

mediaDisplay.onUserInput:
handle = "false";
userInput = currentUserInput();
if ("enter" == userInput) {
	rss = "rss_file://./setup_menu/skin4/scripts/widget.rss";
	ret = doModalRss(rss);
	redrawDisplay("no");
	redrawDisplay("clear");
	if (ret != null) {
		inputKey = getStringArrayAt(ret, 0);
		returnVal = 0 + getStringArrayAt(ret, 1);
		queryIndex = getQueryItemIndex();
		if (queryIndex == 1) {
			lipSyncValue = returnVal;
		}

		target = queryIndex;

		if (inputKey == "enter") {
			rss = "rss_file://./setup_menu/skin4/scripts/dynamicPopup.rss";
			confirm = doModalRss(rss, "mediaDisplay", "popupDialog", 0);
			if (confirm == "$[YES]") {
				setHdmiLipSync("$[LIP_SYNC_MANUAL]", lipSyncValue);
				print("[toto] set Lip sync as [manual] value=" + lipSyncValue + " ..... ");
				setReturnString(confirm);
				redrawDisplay("no");
				handle = "return";
			}
			else {
				redrawDisplay("item", target);
				postMessage("enter");
				handle = "true";
			}
		}
		else if (inputKey == "return") {
			setReturnString("");
			redrawDisplay("no");
			handle = "return";
		}
		else {
			if (target == queryIndex) {
				redrawDisplay("no");
			}
			else {
				setFocusItemIndex(target);
				setItemFocus(target);
				redrawDisplay("item", queryIndex);
				redrawDisplay("item", target);
			}
			postMessage("enter");
			handle = "true";
		}
	}
}
else if ("left" == userInput || "right" == userInput) {
	handle = "true";
}
else if ("up" == userInput || "down" == userInput) {
	handle = "true";
}
handle;

title.script:
data = "";
queryIndex = getQueryItemIndex();
if (queryIndex == 1) {
	data = lipSyncValue;
}
data;
