onClick.script:
isDoable = "yes";
if(isRecording() == "true" || isTimeshifting() == "true" || isEpgRunning() == "true"){
	isDoable = "no";
}

rss = "rss_file://./setup_menu/skin4/scripts/dynamicPopup.rss";
if (isDoable == "no") {
	ret = doModalRss(rss, "mediaDisplay", "popupDialog", 0);
}
else {
	ret = doModalRss(rss);
	if( ret != NULL ) {
		setTvRegion(ret);
	}
}

isDoable = "yes";
if(isRecording() == "true" || isTimeshifting() == "true" || isEpgRunning() == "true"){
	isDoable = "no";
}

if (isDoable == "no") {
	link = null;

	rss = "rss_file://./setup_menu/skin4/scripts/dynamicPopup.rss";
	ret = doModalRss(rss, "mediaDisplay", "popupDialog", 1);
}
else {
	link = "rss_file://./setup_menu/skin4/scripts/channelScan.rss";
}
link;

