rss.onEnter:
pageTitle = getPageInfo("pageTitle");
print("page title is ", pageTitle);

ret = wiredLanDhcpStart();
if( ret == "E_FAIL" ) {
	state = "FAIL";
	header = "$[CONNECTION_TEST]";
	infoStr = "$[NETWL_TEST_FAIL].";
	prompt = "$[OK]";
}
else {
	header = "$[CONNECTION_TEST]";
	state = wiredLanDhcpQueryState();
	infoStr = "$[NETWL_TEST_CONN]...";
	prompt = "$[CANCEL]";
	setRefreshTime(500);
}

rss.onRefresh:
newState = wiredLanDhcpQueryState();
if( state != newState ) {
	if( newState == "OK" ) {
		setRefreshTime(-1);
		wiredLanDhcpStop("COMPLETE");

		doModalRss("rss_file://./setup_menu/skin4/scripts/dynamicPopup.rss", "mediaDisplay", "popupDialog", 0);
		redrawDisplay("no");
		postMessage("return");
	}
	else if( newState == "FAIL" ) {
		setRefreshTime(-1);
		wiredLanDhcpStop("COMPLETE");

		infoStr = "$[NETWL_TEST_FAIL].";
		prompt = "$[OK]";
		redrawDisplay();
	}

	state = newState;
}

image.script:
image = "";
queryIndex = getQueryItemIndex();
focusIndex = getFocusItemIndex();
if (queryIndex == focusIndex) {
	image = "./setup_menu/skin4/image/398_focus.fsp";
}
image;

foregroundColor.script:
fgcolor = "255:255:255";
queryIndex = getQueryItemIndex();
focusIndex = getFocusItemIndex();
data = getStringArrayAt(popupOptions, queryIndex);
if (queryIndex == focusIndex) {
	fgcolor = "0:153:255";
}
fgcolor;

mediaDisplay.onEnter:
            ip = "IP: " + getWiredLanIpAddr("IP", "SINGLE");
            submask = "Submask: " + getWiredLanIpAddr("SUBMASK", "SINGLE");
            gateway = "Gateway: " + getWiredLanIpAddr("GATEWAY", "SINGLE");

			popupHeader = "$[NETWL_TEST_CONN]";
			popupMessageSize = 6;
			popupMessages = pushBackStringArray(popupMessages, "$[CONNECTED]");
			popupMessages = pushBackStringArray(popupMessages, "$[PRESS_ENTER_CONTINUE]");
			popupMessages = pushBackStringArray(popupMessages, "");
			popupMessages = pushBackStringArray(popupMessages, ip);
			popupMessages = pushBackStringArray(popupMessages, submask);
			popupMessages = pushBackStringArray(popupMessages, gateway);
			popupOptionSize = 0;
			popupOptionWide = 2;

mediaDisplay.onUserInput:
handle = "false";
userInput = currentUserInput();
if ( "enter" == userInput || "return" == userInput ) {
	if( (state == "OK") || (state == "FAIL") ) {
		handle = "return";
	}
	else {
		setRefreshTime(-1);
		wiredLanDhcpStop("CANCEL");
		handle = "return";
	}

	if (state != NULL) {
		setReturnString(state);
	}
}
else if ("right" == userInput || "left" == userInput) {
	handle = "true";
}
handle;
