rss.onEnter:
	pageTitle = getPageInfo("pageTitle");
	print("page title is ", pageTitle);

	initFlag = getCurrentSetting("$[INIT_SETUP]");
	if (initFlag == "$[OFF]") {
		changeCategory = getEnv("changeCategory");
		if (changeCategory == "previous" || changeCategory == "next") {
			postMessage("return");
			autoSelect = "false";
		}
	}
	else if (initFlag == "$[ON]") {
		if (autoSelect == "true") {
			redrawDisplay("no");
			postMessage("return");
			autoSelect = "false";
		}
		else {
			autoSelect = "true";
		}
	}

	disableDrawBackBuffer = getEnv("disableDrawBackBuffer");
	if (disableDrawBackBuffer == "true") {
		redrawDisplay("no");
		if (changeCategory == "") {
			setEnv("disableDrawBackBuffer", "false");
		}
	}

	targetTitle = "$[NETWORK]";
	if (initFlag == "$[ON]") {
		targetTitle = "$[INIT_SETUP]";
	}
	itemTitleCount = getEnv("itemTitleCount");
	itemTitleList = getEnv("itemTitleList");
	itemTitleIndex = 0;
	checkIndex = 0;
	while (1) {
		checkTitle = getStringArrayAt(itemTitleList, checkIndex);
		if (checkTitle == targetTitle) {
			itemTitleIndex = checkIndex;
		}
		checkIndex += 1;
  		if (checkIndex == itemTitleCount){
			break;
  		}
	}

image.script:
if (initFlag == "$[ON]") {
	"./setup_menu/skin4s/image/setup_p2_box_top_1.bmp";
}
else if (itemTitleCount == 4) {
	"./setup_menu/skin4s/image/setup_p2_box_top_4.bmp";
}
else if (itemTitleCount == 5) {
	"./setup_menu/skin4s/image/setup_p2_box_top_5.bmp";
}
else {
	"./setup_menu/skin4s/image/setup_p2_box_top_6.bmp";
}

text.script:
if (itemTitleCount > 4) {
	index = (itemTitleIndex + 4) % itemTitleCount;
	title = getStringArrayAt(itemTitleList, index);
}
else {
	title = "";
}

offsetXPC.script:
nextPos = hintIconX;
currPos = nextPos;
nextPos += hintIconWidth + hintGap;
currPos;

currPos = nextPos;
nextPos += hintIconWidth + hintGap;
currPos;

currPos = nextPos;
nextPos += hintMsgWidth + hintGap;
currPos;

text.script:
if (initFlag == "$[ON]") {
	nextPos = hintIconX;
	currPos = nextPos;
	"";
}
else {
	"$[CHANGE_CATEGORY]";
}

image.script:
image = "";
queryIndex = getQueryItemIndex();
focusIndex = getFocusItemIndex();
if (queryIndex == focusIndex) {
	image = "./setup_menu/skin4s/image/setup_p2_list_focus.bmp";
}
image;

foregroundColor.script:
fgcolor = "255:255:255";
enable = getWiredLanDhcpEnable();
title = getItemInfo("title");
if (enable == "$[ON]") {
	if (title == "$[NET_AUTO_IP]") {
		fgcolor = "253:184:50";
	}
}
else {
	if (title == "$[NET_MENU_IP]") {
		fgcolor = "253:184:50";
	}
}
fgcolor;

mediaDisplay.onEnter:
hintIconX = 11.4;
hintIconY = 84.7;
hintIconWidth = 2.0;
hintIconHeight = 3.6;
hintGap = 0.9;
hintMsgY = 85.6;
hintMsgWidth = 17.2;
hintMsgHeight = 2.8;

initFlag = getCurrentSetting("$[INIT_SETUP]");
if (initFlag == "$[ON]") {
	print("initFlag = ", initFlag);
}

mediaDisplay.onUserInput:
handle = "false";
userInput = currentUserInput();
if ("left" == userInput) {
	redrawDisplay("no");
	setEnv("disableDrawBackBuffer", "true");
	setEnv("changeCategory", "previous");
	handle = "return";
}
else if ("right" == userInput) {
	redrawDisplay("no");
	setEnv("disableDrawBackBuffer", "true");
	setEnv("changeCategory", "next");
	handle = "return";
}
handle;

onClick.script:
ret = doModalRss("rss_file://./setup_menu/skin4s/scripts/wiredLanFixIpManual.rss");

initFlag = getCurrentSetting("$[INIT_SETUP]");
if (initFlag == "$[ON]") {
	if (autoSelect == "true") {
		redrawDisplay("no");
		postMessage("return");
		autoSelect = "false";
	}
	else {
		autoSelect = "true";
	}
}
null;
